def main():    N = int(input())    ar = sorted(list(map(int, str(input()).split())))    prs = ''    mini = pow(10, 7) + 1    for i in range(1, N):        diff = abs(ar[i-1] - ar[i])        if (diff <= mini):            if (diff < mini):                prs = ''            mini = diff            prs += str(ar[i-1]) + ' ' + str(ar[i]) + ' '    print (prs)if __name__ == '__main__':    main()import sysinput()array = sorted(map(int, input().split()))smallest = sys.maxsizepairs = []for i in range(1,len(array)):    diff = abs(array[i-1] - array[i])    if diff < smallest:        smallest = diff        pairs = []        pairs.append(array[i-1])        pairs.append(array[i])    elif diff == smallest:        pairs.append(array[i-1])        pairs.append(array[i])print(" ".join(map(str,pairs)))import sysdef print_tuples(ts):    for a, b in ts:        print (a, b, end=" ")def closest_numbers(a):    a = sorted(a)    pairs = []    current_min = max(a)    for idx in range(1, len(a)):        diff = a[idx] - a[idx - 1]        if (a[idx] - a[idx - 1] == current_min):            pairs.append((a[idx - 1], a[idx]))        elif (diff < current_min):            pairs = []            current_min = a[idx] - a[idx - 1]            pairs.append((a[idx - 1], a[idx]))    return pairsif __name__ == '__main__':    n = input()    a = map(int, input().strip().split(" "))    ts = closest_numbers(a)    print_tuples(ts)